CREATE TABLE A (
  ID INT64 NOT NULL,
) PRIMARY KEY(ID);

CREATE INDEX IDX_A ON A(ID);

CREATE TABLE B (
  ID INT64 NOT NULL,
  CONSTRAINT TC_B FOREIGN KEY(ID) REFERENCES A(ID),
) PRIMARY KEY(ID);

ALTER TABLE A ADD CONSTRAINT TC_A FOREIGN KEY(ID) REFERENCES B(ID);

CREATE TABLE Root (
  ID INT64 NOT NULL,
) PRIMARY KEY(ID);

CREATE TABLE Child (
  ID INT64 NOT NULL,
) PRIMARY KEY(ID),
  INTERLEAVE IN PARENT Root ON DELETE NO ACTION;

CREATE TABLE GrandChild (
  ID INT64 NOT NULL,
) PRIMARY KEY(ID),
  INTERLEAVE IN PARENT Child ON DELETE NO ACTION;
